import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';

ReactDOM.render(<App />, document.getElementById('root'));

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();

function Dials(props) {
    console.log("HI!",props);
    return(
        // This should be a list <li>
        <div className="signal-dials">
            <div>
                id: {props.id}
                amp: {props.amplitude}
                freq: {props.frequency}
            </div>
            <input type="range" min={1} max ={10} value = {1}/> 
        </div>
    );
}

class Controls extends React.Component {

    renderDial(){
        console.log("Hi from renderDial",this.props);
        console.log("Hi from renderDial",this.props.signals[0].id);
        return(
            <Dials
                id = {this.props.signals[0].id}
            />
        );
    }

    render(){
        console.log("Hi from Controls render",this.props);
        return(
            <div className = "dials" >
            <div id="add-signal"> Add New Signal </div>
            <ol>{this.renderDial(this.props)}</ol>
            </div>
        );
    }
}

class Home extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            signals: [{
                id: 0,
                amplitude: 0,
                frequency: 0,
            },{
                id: 1,
                amplitude: 1,
                frequency: 1,
            }],
        };

    }

    // https://stackoverflow.com/questions/47581967/reactjs-failed-to-compile-objects-is-not-defined-no-undef
    
    addSignal(){
        const signals = this.state.signals;

        this.setState({
            signals: signals.concat([{  // Just concatenating array of JSON
                id: signals.length - 1,
                amplitude: 1,   // Should be user input
                frequency: 1,   // Should be user input
            }]),
        });
    }
    

    removeSignal(){
        // Need to find signal id from the list and delete
    }

    adjustSignal(){
        // 
    }

    render() {
    
    const current = this.state.signals;

    return (
            <div className = "container">
                <div className = "canvas"></div>

                <div className = "sidebar">
                    <div className = "tab" id="home"> Home </div>
                    <div className = "tab"> Topic 1 </div>
                    <div className = "tab"> Topic 2 </div>
                    <div className = "tab"> Topic 3 </div> 			
                </div>

                <div className = "controls">
                    
                        <Controls
                            signals = {current}
                        />
                    
                </div>
            </div>
      );
    }
  }

  
  // ========================================
  
  ReactDOM.render(
    <Home />,
    document.getElementById('root')
  );
